INSERT INTO `fa_category` VALUES ('21', '0', 'product', '产品中心', 'Product Center', 'index,recommend', '/uploads/20180411/56b000b60601dc4d99f382d9865b967c.jpg', '', '力格尔公司是集研发、制造、经营为一体的高新技术企业。产品制造由两大部分组成：一是民用科技产品设备制造（安防工程设计及有关电子产品研发和设备制造）；二是军用高科技产品设备制造（有关激光产品研发和机械设备制造如：夜视仪、飞机自动装弹系统、激光驱鸟器）。我们有能力和实力为国防建设保驾护航；为国家建设和民生福祉安防创造平安的绿色生态。', '', '1523510808', '1523954652', '21', 'normal', '', '');
